mapper.select:
select * 
  from basket 
 where basket_member_id=#{basket_member_id} 
   and basket_goods_num=#{basket_goods_num}

mapper.update:
update basket
   set basket_goods_amount=basket_goods_amount+#{basket_goods_amount}
 where basket_member_id=#{basket_member_id}
   and basket_goods_num=#{basket_goods_num}
