mapper.select:
		select count(goods_num) from goods
         
        
        
        
         
         
        goods_category like #{find_category}
        
        
        
        
        and goods_level =#{find_level}
        
		 and goods_inuse=0

select * from ( select rowNum rNum, goods_num, goods_category, goods_level, goods_name, goods_content, goods_price, goods_image, goods_best, goods_date from (select * from goods
goods_category like #{find_category}
and goods_level =#{find_level}
and goods_inuse=0
order by goods_num desc)) where rNum > #{startrow} and rNum<#{endrow}
